// front-end/components/DrugExplanationCard.jsx
import React from "react";

const DrugExplanationCard = ({ drugName, explanation }) => {
  if (!explanation) return null;

  // explanation beklenen yapı:
  // {
  //   usage: ["...","..."],
  //   side_effects: ["..."],
  //   warnings: ["..."],
  //   raw_text: "orijinal serbest metin"
  // }

  const usageList = Array.isArray(explanation.usage) ? explanation.usage : (explanation.usage ? [explanation.usage] : []);
  const sideEffects = Array.isArray(explanation.side_effects) ? explanation.side_effects : (explanation.side_effects ? [explanation.side_effects] : []);
  const warnings = Array.isArray(explanation.warnings) ? explanation.warnings : (explanation.warnings ? [explanation.warnings] : []);

  return (
    <div style={styles.card}>
      <h2 style={{ marginTop: 0 }}>{drugName || "Bilinmeyen İlaç"}</h2>

      <div>
        <strong>Kullanım:</strong>
        {usageList.length > 0 ? (
          <ul>
            {usageList.map((u, i) => <li key={i}>{u}</li>)}
          </ul>
        ) : (
          <p>Bilgi yok</p>
        )}
      </div>

      <div>
        <strong>Yan etkiler:</strong>
        {sideEffects.length > 0 ? <p>{sideEffects.join(", ")}</p> : <p>Yok / Bilinmiyor</p>}
      </div>

      <div>
        <strong>Uyarılar:</strong>
        {warnings.length > 0 ? <ul>{warnings.map((w,i)=>(<li key={i}>{w}</li>))}</ul> : <p>Yok / Bilinmiyor</p>}
      </div>

      {explanation.raw_text && (
        <details style={{ marginTop: 8 }}>
          <summary>Ham LLM Çıktısı</summary>
          <pre style={{ whiteSpace: "pre-wrap", textAlign: "left" }}>{explanation.raw_text}</pre>
        </details>
      )}
    </div>
  );
};

const styles = {
  card: {
    border: "1px solid #ccc",
    padding: "20px",
    borderRadius: "10px",
    maxWidth: "700px",
    margin: "20px auto",
    backgroundColor: "#f9f9f9",
    textAlign: "left",
  },
};

export default DrugExplanationCard;
