import React, { useState } from "react";
import ImageUpload from "./components/ImageUpload";
import TextInputForm from "./components/TextInputForm";
import DrugExplanationCard from "./components/DrugExplanationCard";

function App() {
  const [inputType, setInputType] = useState(null); // "image" veya "text"
  const [drugName, setDrugName] = useState("");
  const [explanation, setExplanation] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

const handleImageUpload = async (file) => {
  setLoading(true);
  setError("");
  setExplanation(null);
  setDrugName("");

  const formData = new FormData();
  formData.append("image", file);

  try {
    const res = await fetch("/get-drug-info", {
  method: "POST",
  headers: { "Content-Type": "application/json" },
  body: JSON.stringify({ drug_name: drug }),
});

    if (!res.ok) throw new Error("G√∂rsel y√ºkleme veya bilgi alma hatasƒ±");

    const data = await res.json();

    if (data.error) throw new Error(data.error);

    setDrugName(data.drug_name);
    setExplanation(data);
  } catch (err) {
    setError(err.message || "Bir hata olu≈ütu");
  } finally {
    setLoading(false);
  }
};

const handleTextSubmit = async (drug) => {
  setLoading(true);
  setError("");
  setExplanation(null);
  setDrugName("");

  try {
    setDrugName(drug);
    const res = await fetch("http://localhost:5000/get-drug-info", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ drug_name: drug }),
    });

    if (!res.ok) throw new Error("ƒ∞la√ß bilgisi alƒ±namadƒ±");

    const data = await res.json();
    if (data.error) throw new Error(data.error);

    setExplanation(data);
  } catch (err) {
    setError(err.message || "Bir hata olu≈ütu");
  } finally {
    setLoading(false);
  }
};


  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h1>üíä ƒ∞la√ß Tanƒ±ma ve A√ßƒ±klama Sistemi</h1>

      {!inputType && (
        <div>
          <button onClick={() => setInputType("image")} style={btnStyle}>
            üì∑ G√∂rsel Y√ºkle
          </button>
          <button onClick={() => setInputType("text")} style={btnStyle}>
            ‚úç ƒ∞la√ß Yaz
          </button>
        </div>
      )}

      {inputType === "image" && (
        <ImageUpload onImageUpload={handleImageUpload} loading={loading} />
      )}
      {inputType === "text" && (
        <TextInputForm onSubmitDrug={handleTextSubmit} loading={loading} />
      )}

      {loading && <p>Y√ºkleniyor...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}

      <DrugExplanationCard
        drugName={drugName}
        explanation={explanation}
      />
    </div>
  );
}

const btnStyle = {
  margin: "10px",
  padding: "10px 20px",
  fontSize: "16px",
  borderRadius: "8px",
  border: "none",
  backgroundColor: "#28a745",
  color: "white",
  cursor: "pointer",
};

export default App;
