import React, { useState } from "react";
import ImageUpload from "./components/ImageUpload";
import TextInputForm from "./components/TextInputForm";
import DrugExplanationCard from "./components/DrugExplanationCard";

function App() {
  const [inputType, setInputType] = useState(null); // "image" veya "text"
  const [drugName, setDrugName] = useState("");
  const [explanation, setExplanation] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  // G√∂rsel y√ºklendiƒüinde (VLM i≈ülemi yapƒ±lacak yer)
  const handleImageUpload = async (file) => {
    setLoading(true);
    setError("");
    setExplanation("");
    setDrugName("");

    const formData = new FormData();
    formData.append("image", file);

    try {
      // G√∂rseli backend'e g√∂nder
      const res1 = await fetch("http://localhost:5000/upload-image", {
        method: "POST",
        body: formData,
      });

      if (!res1.ok) throw new Error("G√∂rsel y√ºkleme hatasƒ±");

      const data1 = await res1.json();
      const drug = data1.drug_name;
      setDrugName(drug);

      // ƒ∞la√ß bilgisi i√ßin LLM √ßaƒürƒ±sƒ±
      const res2 = await fetch("http://localhost:5000/get-drug-info", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ drug_name: drug }),
      });

      if (!res2.ok) throw new Error("ƒ∞la√ß bilgisi alƒ±namadƒ±");

      const data2 = await res2.json();
      setExplanation(
        `Kullanƒ±m: ${data2.usage}\nYan etkiler: ${data2.side_effects.join(
          ", "
        )}\nUyarƒ±lar: ${data2.warnings.join(", ")}`
      );
    } catch (err) {
      setError(err.message || "Bir hata olu≈ütu");
    } finally {
      setLoading(false);
    }
  };

  // ƒ∞la√ß adƒ± yazƒ±ldƒ±ƒüƒ±nda (LLM i≈ülemi yapƒ±lacak yer)
  const handleTextSubmit = async (drug) => {
    setLoading(true);
    setError("");
    setExplanation("");
    setDrugName("");

    try {
      setDrugName(drug);

      const res = await fetch("http://localhost:5000/get-drug-info", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ drug_name: drug }),
      });

      if (!res.ok) throw new Error("ƒ∞la√ß bilgisi alƒ±namadƒ±");

      const data = await res.json();
      setExplanation(
        `Kullanƒ±m: ${data.usage}\nYan etkiler: ${data.side_effects.join(
          ", "
        )}\nUyarƒ±lar: ${data.warnings.join(", ")}`
      );
    } catch (err) {
      setError(err.message || "Bir hata olu≈ütu");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h1>üíä ƒ∞la√ß Tanƒ±ma ve A√ßƒ±klama Sistemi</h1>

      {!inputType && (
        <div>
          <button onClick={() => setInputType("image")} style={btnStyle}>
            üì∑ G√∂rsel Y√ºkle
          </button>
          <button onClick={() => setInputType("text")} style={btnStyle}>
            ‚úç ƒ∞la√ß Yaz
          </button>
        </div>
      )}

      {inputType === "image" && (
        <ImageUpload onImageUpload={handleImageUpload} loading={loading} />
      )}
      {inputType === "text" && (
        <TextInputForm onSubmitDrug={handleTextSubmit} loading={loading} />
      )}

      {loading && <p>Y√ºkleniyor...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}

      <DrugExplanationCard drugName={drugName} explanation={explanation} />
    </div>
  );
}

const btnStyle = {
  margin: "10px",
  padding: "10px 20px",
  fontSize: "16px",
  borderRadius: "8px",
  border: "none",
  backgroundColor: "#28a745",
  color: "white",
  cursor: "pointer",
};

export default App;
